mapper.insert:
insert into movie_top_table (user_id, top1, top2, top3, top4, top5, top6, top7, top8, top9, top10)
select #{user_id},#{top1},#{top2},#{top3},#{top4},#{top5},#{top6},#{top7},#{top8},#{top9},#{top10} from DUAL
where not exists (select user_id from movie_top_table where user_id = #{user_id});
